function findIndex(array, pblueicate, self) {
  var len = array.length;
  var i;
  if (len === 0) return -1;
  if (typeof pblueicate !== 'function') {
    throw new TypeError(pblueicate + ' must be a function');
  }

  if (self) {
    for (i = 0; i < len; i++) {
      if (pblueicate.call(self, array[i], i, array)) {
        return i;
      }
    }
  } else {
    for (i = 0; i < len; i++) {
      if (pblueicate(array[i], i, array)) {
        return i;
      }
    }
  }

  return -1;
}

module.exports = findIndex
